mapper.insert:
INSERT INTO USER
(
    USER_ID
,   PASSWORD
,   NAME
,   EMAIL
,   PHONE
,   ADDRESS
,   DETAIL_ADDRESS
,   DELETE_AT
,   CREATED_DATE
) VALUES (
    #{userId}
,   #{password}
,   #{name}
,   #{email}
,   #{phone}
,   #{address}
,   #{detailAddress}
,   'N'
,   #{createdDate}
)

mapper.select:
SELECT COUNT(*)
FROM USER
WHERE USER_ID = #{userId}
AND PASSWORD = #{password}
AND DELETE_AT = 'N'
